import React, { useContext } from 'react'
import { UserContext } from './A'
import C from './C'
const B = () => {
  //using context
  let {name,setname}=useContext(UserContext)

  function changeName(){
    setname("vamsi krishna")
  }
  return (
    
    <div>
      <h1>{name}</h1>
      <button onClick={function(){
       setname("vamsi")
      }}>changeName</button>
    </div>
  )
}

export default B
